    def control_base(self,xx,yy,pos,color,skip =True):
        control = False
        added = list()
        for i,j in pos:
            x,y = i+xx,yy+j
            if x >= len(self.board) or y >= len(self.board):
                self.delete(added)
                return False
            # kontrola jesti se pole vejde, případne vepsání 9
            if self.board[x][y] > 0:
                self.delete(added)
                return False
            else:
                self.board[x][y] = 9
                added.append([x,y])

            # kontrola jesti se neporuší pravidlo o čtevrcích
            if self.control_rect(x,y):
                self.delete(added)
                return False

            # kontrola jestli se neporuší pravidlo barev
            if skip:
                c2 = self.control_color(color,x,y)
                if c2 == 0:
                    self.delete(added)
                    return False
                elif c2 == 1:
                    control = True
            else:
                control = True
        if control:
            self.delete(added)
            return True
        self.delete(added)
        return False
